mapper.insert:

	    	INSERT INTO family_information
			(
			    id
			    , name
			    , relationship
			    , phone
			    , description
			    , create_by
			    , create_date
			    , status
			)
			VALUES (
			#{id}
			, #{name}
			, #{relationship}
			, #{phone}
			, COALESCE(#{description}, '' )
            , #{userId}
			, to_char(NOW(), 'YYYYMMDD')
			, #{status}
			)

mapper.update:

				UPDATE family_information
				SET
                    name		        = #{name}
					, relationship		= #{relationship}
					, phone	            = #{phone}
                    , description	    = COALESCE(#{description}, '' )
                    , modify_date	    = to_char(NOW(), 'YYYYMMDD')
                    , modify_by		    = #{userId}
				WHERE id			    = #{id}
