mapper.insert:

    	INSERT INTO product
		(
		    id
		    , name
		    , category_id
		    , resource_img_id
			, description
			, create_date
			, create_by
			, status
		)
		VALUES (
		  #{id}
		, #{name}
		, #{category_id}
		, COALESCE(#{resource_img_id}, '' )
		, COALESCE(#{description}, '' )
		, to_char(NOW(), 'YYYYMMDD')
		, #{user_id}
		, #{status}
		)

mapper.update:

UPDATE product
SET
      name 			    = #{name}
    , category_id  		= #{category_id}
    , resource_img_id  	= #{resource_img_id}
    , description 	    = COALESCE(#{description},'')
    , status		    = #{status}
    , modify_by		    = #{user_id}
    , modify_date	    = to_char(NOW(), 'YYYYMMDD')
WHERE id 			    = #{id}


